const form = document.getElementById('form');
const username = document.getElementById('username');
const email = document.getElementById('email');
const password = document.getElementById('password');
const passwordConfirm = document.getElementById('password-confirm');
const showPassword = document.querySelector('.bi-eye-slash');
function showError(element, message) {
    const formControl = element.parentElement;
    const error = formControl.querySelector('small');
    error.innerText = message;
    formControl.className = 'form-control error';

}

function showSuccess(element) {
    const formControl = element.parentElement;
    formControl.className = 'form-control success'
}

//Validate Email
function validateEmail(email) {
    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    re.test(email.value) ? showSuccess(email) : showError(email, 'Please enter a valid email')
}

function checkRequired(inputArr) {
    inputArr.forEach(input => {
        if (input.value.trim() == '') {
            showError(input, `${getInput(input.id)} is required`)
        } else {
            showSuccess(input)
        }
    })
}
//functions

/*************Check Length ******************/
function checkLength(input, min, max) {
    if (input.value.length < min) {
        showError(input, `${getInput(input.id)} must be atleast ${min} characters long`)
    } else if (input.value.length >= max) {
        showError(input, `${input.id} must be less than ${max} characters long`)
    } else {
        showSuccess(input)
    }
}


function checkPassword(password) {
    var passw = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,20}$/;
    if (passw.test(password.value)) {
        showSuccess(password)
    }
    else {
        showError(password, 'Password must be 6 to 20 characters which contain at least one numeric digit, one uppercase and one lowercase letter')
    }
}

function checkPasswordsMatch(input1,input2) {
    input1.value == input2.value ? showSuccess(input2) : showError(input2, 'Passwords do not match')
}
function getInput(input) {
    return input.charAt(0).toUpperCase() + input.slice(1);
}

form.addEventListener('submit', function (e) {
    e.preventDefault();
    const inputArray = [username, email, password,passwordConfirm];
    checkRequired(inputArray);
    validateEmail(email);
    checkLength(username, 5, 15);
    checkPassword(password);
    checkPasswordsMatch(password,passwordConfirm);

})

/*************Show Password ******************/

showPassword.addEventListener('click', togglePasswords);

function togglePasswords(input) {
    const type = password.getAttribute('type') === 'password' ? 'text' : 'password';
    password.setAttribute('type', type);
    this.classList.toggle('bi-eye');
}


