@font-face {font-family: "Centra No2";
    src: url("../fonts/Centra\ No2.ttf"); /* IE9*/
}

@font-face {
    font-family: 'Ivy-mode regular';
    src: url('../fonts/IvyMode-Regular.ttf');
}

:is(h1, h2, h3, h4, h5, h6) {
    font-family: 'Ivy-mode regular' !important;
    color: #fff;
  }

  .h1-hero {
      font-size: 100px;
      font-family: 'Ivy-mode regular' !important;

  }
  p {
    font-family: "Centra No2";
    color: #fff;
    font-size: 18px;
  }

  .hero {
      h1,p {
          text-align: center;
      }
  }
  
  .top-text {
    color: #106c8e !important;
    font-family: "Centra No2";
