    color: #121110;
    font-size: 30px;
    line-height: 44px;
    font-weight: 400;
    letter-spacing: 1px;
    text-transform: capitalize;
}

.room-slider {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 50px;
}

.room-block {
    width: 100%;

