$orange: #c65624;


ul li {
    display: inline;
}

.book-now-btn {
    padding: 16px 30px 14px;
    border-style: solid;
    border-width: 1.5px;
    border-color: rgb(255, 255, 255);
    color: rgb(255, 255, 255);
    background: transparent;
    color: #fff;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: all 0.5s ease-in;
    font-family: "Centra No2";

    &:hover {
        background: #fff;
        color: #000;

    }
}

.book-now-orange {
    @extend .book-now-btn;
    border-color: $orange;
    background: $orange;
    align-self: center;
    background-color: $orange;

    &:hover {
        background-color: #a3441a;
        color: #fff;
        border-color: #a3441a;
    }

}

.book-now-light {
    @extend .book-now-btn;
    color: $orange;
    border: 1px solid $orange;
}
.menu-mobile.open {
    opacity: 1;
    transform: translate3d(0px, 110vh, 0px) scale3d(1, 1, 1) rotateX(0deg) rotateY(0deg) rotateZ(0deg) skew(0deg,
            0deg);
    transform-style: preserve-3d;
}
.menu-mobile {
    position: fixed;
    top: -100vh;
    z-index: 10;
    display: block;
    overflow: auto;
    width: 100vw;
    height: 100vh;
    padding: 32px 16px;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -ms-flex-pack: center;
    justify-content: center;
    background-color: #2d4d3a;
    opacity: 1;
    transition: all 1s ease-in;
