@media only screen and (max-width: 768px) {

    .hamburger {
        top: 50%;
        left: 50%;
        width: 20px;
        height: 14px;
        transform: translate(-800%, -50%);

        span {
            position: relative;
            display: block;
            width: 20px;
            height: 2px;
            margin-top: 6px;
            background: #fff;
            transition: all 0.4s;

            &:before,
            &:after {
                content: '';
                display: block;
                width: 100%;
                height: 2px;
                left: 0;
                background: #fff;
                position: absolute;
                transition: all 0.4s;
            }

            &:before {
                top: -6px;
            }
        }

        &.hamburger-5 {
            transform: translate(0, -50%);
        }

        &.close {
            &.hamburger-5 {
                span {
                    transform: rotate(180deg);
                    background: transparent;

                    &:before {
                        top: 0;
                        transform: rotate(45deg);
                    }

                    &:after {
                        bottom: 0;
                        transform: rotate(-45deg);
                    }
                }
            }
        }
    }

    .menu-button {
        width: 50px;
        padding-right: 16px;
        padding-left: 0px;
        z-index: 1;
    }

    .book-now-orange {
        grid-column: span 2;
        margin: 10px 0;
        padding: 16px 30px 14px !important;
    }

    .book-now-btn {
        padding: 5px;

        a {
