$orange: #c65624;


ul li {
    display: inline;
}

.book-now-btn {
    padding: 16px 30px 14px;
    border-style: solid;
    border-width: 1.5px;
    border-color: rgb(255, 255, 255);
    color: rgb(255, 255, 255);
    background: transparent;
    color: #fff;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: all 0.5s ease-in;
    font-family: "Centra No2";

    &:hover {
        background: #fff;
        color: #000;

    }
}

.book-now-orange {
    @extend .book-now-btn;
    border-color: $orange;
    background: $orange;
    align-self: center;
    background-color: $orange;

    &:hover {
        background-color: #a3441a;
        color: #fff;
        border-color: #a3441a;
    }

}

.menu-mobile.open {
    opacity: 1;
    transform: translate3d(0px, 110vh, 0px) scale3d(1, 1, 1) rotateX(0deg) rotateY(0deg) rotateZ(0deg) skew(0deg,
            0deg);
    transform-style: preserve-3d;
}
.menu-mobile {
    position: fixed;
    top: -100vh;
    z-index: 10;
    display: block;
    overflow: auto;
    width: 100vw;
    height: 100vh;
    padding: 32px 16px;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -ms-flex-pack: center;
    justify-content: center;
    background-color: #2d4d3a;
    opacity: 1;
    transition: all 0.5s ease-in;
    font-family: 'Ivy-mode regular';
    text-align: center;
}

.mobile-nav-links {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.nav-link-mobile {
    padding-top: 16px;
    padding-bottom: 16px;
    font-size: 24px;
}
@media only screen and (max-width: 768px) {

    .hamburger {
        top: 50%;
        left: 50%;
        width: 20px;
        height: 14px;
        transform: translate(-800%, -50%);

        span {
            position: relative;
            display: block;
            width: 20px;
            height: 2px;
            margin-top: 6px;
            background: #fff;
            transition: all 0.4s;

            &:before,
            &:after {
                content: '';
                display: block;
                width: 100%;
                height: 2px;
                left: 0;
                background: #fff;
                position: absolute;
                transition: all 0.4s;
            }

            &:before {
                top: -6px;
            }
        }

        &.hamburger-5 {
            transform: translate(0, -50%);
        }

        &.close {
            &.hamburger-5 {
                span {
                    transform: rotate(180deg);
                    background: transparent;

                    &:before {
                        top: 0;
                        transform: rotate(45deg);
                    }

                    &:after {
                        bottom: 0;
                        transform: rotate(-45deg);
                    }
                }
            }
        }
    }

    .menu-button {
        width: 50px;
        padding-right: 16px;
        padding-left: 0px;
        z-index: 1;
    }

    .book-now-orange {
        grid-column: span 2;
        margin: 10px 0;
        padding: 16px 30px 14px !important;
    }

    .book-now-btn {
        padding: 5px;

        a {
            font-size: 12px;
        }
    }

    .property-name {
        display: none;
    }
}

.menu-link {
    font-weight: bold;
}
.orange-text {
    color: #ff9363 !important;
}
.mobile-menu-links {
    padding-top: 32px;
    border-top: 1px solid rgba(255, 255, 255, 0.2);
    font-family: "Centra No2";
}

.menu-link-group {
    margin-bottom: 24px;
}
.menu-titles {
    font-size: 12px;
    line-height: 1.3;
    font-weight: 500;
}
.social-links-menu {
    display: flex;
    justify-content: center;
    margin-bottom: 50px;
}

.social-icon-menu {
    margin: 0 20px;
}

