$orange: #c65624;


nav {
    display: flex;
    height: 10vh;
    justify-content: space-around;
    align-items: center;
    background: transparent;
    color: #fff;
    font-family: "Centra No2";
}

header {
    max-width: 1440px;
    margin: auto;
    font-family: "Centra No2";
    color: #fff;
}

/*Hero Section */
.hero {
    position: relative;
    height: 90vh;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}


.cover-image {
    position: absolute;
    background-image: url('../images/hero-homepage.jpg');
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    width: 100%;
    filter: brightness(0.9);
    height: 100%;
    z-index: -1;
}


.wrapper-class {
    max-width: 1500px;
    margin: auto;
}

.time-block {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: flex-start;
    position: absolute;
    bottom: 10%;
    left: 10%;
}

.site-nav {
    width: 40%;
    justify-content: space-between;
    display: flex;
}

.local-time {
    margin-bottom: -15px;
}

#weather-icon {
    width: 30px;
    height: 30px;
}

#temp-desc {
    text-transform: capitalize;
}


input[type="text"] {
    background: transparent;
    border: none;
}

::-webkit-input-placeholder {
    /* Edge */
    color: $orange;
    font-family: "Centra No2";

}

:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: $orange;
    font-family: "Centra No2";

}

::placeholder {
    color: $orange;
    font-family: "Centra No2";
}

.booking-block {
    background-color: #000;
    width: 100%;
    display: flex;
    justify-content: center;
    padding: 5px;
    position: absolute;
    bottom: 0;

    form {
        display: flex;
    }
}

.form-controller {
    display: flex;
    flex-direction: column;
    color: #fff;
    font-family: "Centra No2";
    border-left: 1px solid #fff;
    padding: 10px;
    margin-right: 20px;
}

select {
    background-color: transparent;
    color: $orange;
    border: none;
    width: 100px;
}

/****** Welcome Section ********/

.welcome {
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    padding-top: 140px;
    padding-bottom: 0px;
    flex-direction: column;

    .welcome-content {
        width: 70%;
    }

    .top-text {
        color: $orange;
        font-family: "Centra No2";
        text-transform: uppercase;
        font-weight: bold;
        letter-spacing: 1px;
        font-size: 13px;
    }

    .top-headline {
        font-family: 'Ivy-mode regular', sans-serif;
        margin-top: 5px;
        margin-bottom: 16px;
        color: #121110;
        font-size: 30px;
        line-height: 44px;
        font-weight: 400;
        letter-spacing: 1px;
    }

    .welcome-subtitle {
        margin-top: 8px;
        color: #000;
        margin-bottom: 120px;
    }
}

.floating-images {
    position: relative;
    z-index: 2;
}

.woman-floating {
    width: 150px;
    height: 150px;
    position: absolute;
    bottom: 0;
}
.resort {
    width: 150px;
    height: 150px;
    position: absolute;
    top: 30%;
    bottom: 0;
    right: -20%;
}

/* Media Queries for Mobile */

@media only screen and (max-width: 768px) {
    nav {
        background-color: #2d4d3a;
    }

    .site-nav {
        display: none;
    }

    .time-block {
        display: none;
    }

    .booking-block form {
        display: grid;
        grid-template-columns: 1fr 1fr;
        width: 100%;

    }

    .booking-block {
        font-size: 12px;

    }

    .form-controller {
        margin: 10px 0;
    }
}

.swiper-container {
    width: 100%;
    height: 100%;
    margin-top: -5%;
  }

  .swiper-button-prev, .swiper-container-rtl .swiper-button-next {
      color: #fff !important;
  }
  .swiper-slide {
    text-align: center;
    font-size: 18px;
    background: #fff;

    /* Center slide text vertically */
    display: -webkit-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
  }

  .swiper-slide img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  .swiper-button-next, .swiper-container-rtl .swiper-button-prev {
    color: #fff !important;
}

